/*eslint-disable*/
import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// nodejs library that concatenates classes
import classNames from "classnames";
import Tooltip from "@material-ui/core/Tooltip";
import Grid from "@material-ui/core/Grid";

// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
// @material-ui/icons
import Favorite from "@material-ui/icons/Favorite";
// core components
import Header from "components/Header/Header.jsx";
import Footer from "components/Footer/Footer.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";
import HeaderLinks from "components/Header/HeaderLinks.jsx";
import Parallax from "components/Parallax/Parallax.jsx";
import NavPills from "components/NavPills/NavPills.jsx";

import landingPageStyle from "assets/jss/material-kit-pro-react/views/landingPageStyle.jsx";
import MoviesCards from 'components/MovieCards/MovieCards'
// Sections for this page
import SectionProduct from "./Sections/SectionProduct.jsx";
import SectionTeam from "./Sections/SectionTeam.jsx";
import SectionWork from "./Sections/SectionWork.jsx";
import Papa from 'papaparse';
import * as d3 from 'd3'
import MovieCards from "components/MovieCards/MovieCards.jsx";
class LandingPage extends React.Component {
  componentDidMount() {
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;

  }
  constructor(props){
    super(props)
    this.state={
      result:null,
      main:<div>LOADING</div>
    }
    fetch("http://localhost:8002/a").then(a=>a.json()).then(data=>{
       this.setState({
         result:data
       })
    })

  }
  change=(name)=>{
    console.log(name)
  }
  getDocHeight=()=>{
    window.onscroll= function (ev){
      if((window.innerHeight+window.scrollY)>document.body.offsetHeight){
        alert("WORKING")
      }
    }
  }
  render() {
    const { classes, ...rest } = this.props;
    this.getDocHeight()
   const main=this.state.result !=null ? this.state.result.map(data=>{
     return <MovieCards state={this.change} title={data}/>
   }):null
    $(window).scoll(()=>{
      if($(window).scrollTop() + $(window).height() == $(document).height()){
        console.log("ASD")
      }
    })


    return (
      <div>
        <Header
          color="transparent"
          brand="MovieRecombe"
          links={<HeaderLinks dropdownHoverColor="info" />}
          fixed
          changeColorOnScroll={{
            height: 300,
            color: "primary"
          }}
          {...rest}
        />

        <Parallax image={require("assets/img/bg8.jpg")} filter="dark">
          <div className={classes.container}>
            <GridContainer>
              <GridItem xs={12} sm={6} md={6}>
                <h1 className={classes.title}>What is Movie-Recombe ? </h1>
                <h4>
                  We recomend movies based on your preferences. We would like you to rate the movies that you have watched 
                  and we will recomend movies based on your choices.
                </h4>
                <br />
                <Button
                  color="primary"
                  size="lg"
                  href="https://www.youtube.com/watch?v=dQw4w9WgXcQ&ref=creativetim"
                  target="_blank"
                >
                  <i className="fas fa-play" />
                  Watch video
                </Button>
                <Tooltip
                id="tooltip-right"
                title="The more movies your rate better we are able to recommend you the movies "
                placement="right"
                classes={{ tooltip: classes.tooltip }}
              >

                <Button
                  style={{margin:20}}
                  color="primary"
                  size="lg"
                  href="https://www.youtube.com/watch?v=dQw4w9WgXcQ&ref=creativetim"
                  target="_blank"
                >
                  <i className="fas fa-play" />
                    Submit Movies
                </Button>
              </Tooltip>
              </GridItem>
            </GridContainer>
          </div>
        </Parallax>
        <GridContainer>
        <div className={classNames(classes.main, classes.mainRaised)}>
            <GridItem>
            <h1>Rate The Movies You have watched</h1>

            </GridItem>

          <Grid container  justify="center" spacing={4} style={{padding:20}}>

            {main}

        </Grid>

                </div>

                </GridContainer>

        <Footer
          content={
            <div>
              <div className={classes.left}>
                <List className={classes.list}>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      College Project
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/presentation?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      About us
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="//blog.creative-tim.com/"
                      className={classes.block}
                    >
                      Blog
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/license?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      Licenses
                    </a>
                  </ListItem>
                </List>
              </div>
              <div className={classes.right}>
                &copy; {1900 + new Date().getYear()} , made with{" "}
                <Favorite className={classes.icon} /> by{" "}
                <a
                  href="https://www.creative-tim.com/?ref=mkpr-landing"
                  target="_blank"
                >
                  Creative Tim
                </a>{" "}
                for a better web.
              </div>
            </div>
          }
        />
      </div>
    );
  }
}

LandingPage.propTypes = {
  classes: PropTypes.object
};

export default withStyles(landingPageStyle)(LandingPage);
