/*eslint-disable*/
import React from "react";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import popper from 'popper.js'
// @material-ui/icons
import Favorite from "@material-ui/icons/Favorite";
// core components
import Header from "components/Header/Header.jsx";
import Footer from "components/Footer/Footer.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import GridItem from "components/Grid/GridItem.jsx";
import Button from "components/CustomButtons/Button.jsx";
import HeaderLinks from "components/Header/HeaderLinks.jsx";
import Parallax from "components/Parallax/Parallax.jsx";
import NavPills from "components/NavPills/NavPills.jsx";

import landingPageStyle from "assets/jss/material-kit-pro-react/views/landingPageStyle.jsx";

// Sections for this page
import SectionProduct from "./Sections/SectionProduct.jsx";
import SectionTeam from "./Sections/SectionTeam.jsx";
import SectionWork from "./Sections/SectionWork.jsx";

class LandingPage extends React.Component {
  componentDidMount() {
    window.scrollTo(0, 0);
    document.body.scrollTop = 0;
    if ( ReactDOM.findDOMNode(this.target) ) { this.initPopper() }

  }

componentDidUpdate(){
    if ( this.popperInstance ) {
        this.popperInstance.update();
    }
}

componentWillUnmount() {
    if ( this.popperInstance ) {
        this.popperInstance.destroy();
    }
}

initPopper() {

  if ( !ReactDOM.findDOMNode(this.target) ) { return }

  // Add position absolute to the popper element for proper positioning.
  ReactDOM.findDOMNode(this.target).setAttribute('style','position:absolute');

  this.popperInstance = new PopperJS(
      ReactDOM.findDOMNode(this.parent), 
      ReactDOM.findDOMNode(this.target), 
      {
          ...this.props.options, // Spread the options provided to the component
          modifiers : {
              applyStyle: {enabled : false},
              updateStateWithStyle: {
                  enabled : true,
                  fn : this.update,
              }
          }
      }
  );
}

update = (data) => {
  this.setState(data);
  return data; // Important! Return data to popper
}

getStyle = (data) => {

  if ( !data || !data.offsets || !data.offsets.popper ) { return }

  const left = data.offsets.popper.left;
  const top = data.offsets.popper.top;
  const transform = `translate3d(${left}px, ${top}px, 0)`;

  return {
      position: data.offsets.popper.position,
      transform,
      WebkitTransform: transform,
      top: 0,
      left: 0,
      willChange: 'transform'
  }

}
  render() {
    const { classes, ...rest } = this.props;
    var c='A';
    var atozee  = []
    for(let i=0;i<26;i++){
      atozee.push({tabButton:c, tabContent: (
        <span>
         {c}
        </span>
      ) });
      c=String.fromCharCode(c.charCodeAt(0) + 1);

    }
    return (
      <div>
        <Header
          color="transparent"
          brand="MovieRecombe"
          links={<HeaderLinks dropdownHoverColor="info" />}
          fixed
          changeColorOnScroll={{
            height: 300,
            color: "info"
          }}
          {...rest}
        />

        <Parallax image={require("assets/img/bg8.jpg")} filter="dark">
          <div className={classes.container}>
            <GridContainer>
              <GridItem xs={12} sm={6} md={6}>
                <h1 className={classes.title}>What is Movie-Recombe</h1>
                <h4>
                  We recomend movies based on your preferences. We would like you to rate the movies that you have watched 
                  and we will recomend movies based on your choices.
                </h4>
                <br />
                <Button
                  color="rose"
                  size="lg"
                  href="https://www.youtube.com/watch?v=dQw4w9WgXcQ&ref=creativetim"
                  target="_blank"
                >
                  <i className="fas fa-play" />
                  Watch video
                </Button>
                <Button
                  style={{margin:20}}
                  color="rose"
                  size="lg"
                  href="https://www.youtube.com/watch?v=dQw4w9WgXcQ&ref=creativetim"
                  target="_blank"
                >
                  <i className="fas fa-play" />
                    Submit Movies
                </Button>
              </GridItem>
            </GridContainer>
          </div>
        </Parallax>
        <GridContainer>

        <div className={classNames(classes.main, classes.mainRaised)}>

          <GridItem xs={12} sm={12} md={6} lg={6}>

          <NavPills
                  horizontal={{
                    tabsGrid: { xs: 12, sm: 4, md: 5 },
                    contentGrid: { xs: 12, sm: 8, md: 7 }
                  }}
                  color="rose"
                  tabs={atozee}
                />

                </GridItem>
                </div>

                </GridContainer>

        <Footer
          content={
            <div>
              <div className={classes.left}>
                <List className={classes.list}>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      Creative Tim
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/presentation?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      About us
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="//blog.creative-tim.com/"
                      className={classes.block}
                    >
                      Blog
                    </a>
                  </ListItem>
                  <ListItem className={classes.inlineBlock}>
                    <a
                      href="https://www.creative-tim.com/license?ref=mkpr-landing"
                      target="_blank"
                      className={classes.block}
                    >
                      Licenses
                    </a>
                  </ListItem>
                </List>
              </div>
              <div className={classes.right}>
                &copy; {1900 + new Date().getYear()} , made with{" "}
                <Favorite className={classes.icon} /> by{" "}
                <a
                  href="https://www.creative-tim.com/?ref=mkpr-landing"
                  target="_blank"
                >
                  Creative Tim
                </a>{" "}
                for a better web.
              </div>
            </div>
          }
        />
      </div>
    );
  }
}

LandingPage.propTypes = {
  classes: PropTypes.object
};

export default withStyles(landingPageStyle)(LandingPage);
